// lib/main.dart
import 'dart:async'; // for runZonedGuarded

import 'package:flutter/material.dart';
import 'package:firebase_core/firebase_core.dart';
import 'firebase_options.dart';
import 'supabase_config.dart';
import 'safe_her_welcome_screen.dart';
import 'manage_contacts_screen.dart';
import 'theme.dart';

Future<void> main() async {
  WidgetsFlutterBinding.ensureInitialized();

  try {
    // ✅ Important on web: initialize with platform options generated by flutterfire
    await Firebase.initializeApp(
      options: DefaultFirebaseOptions.currentPlatform,
    );
    debugPrint('✅ Firebase initialized successfully');

    // Initialize Supabase for OTP authentication
    try {
      await SupabaseConfig.initialize();
      debugPrint('✅ Supabase initialized successfully');
    } catch (supabaseError) {
      debugPrint('⚠️ Supabase initialization failed: $supabaseError');
      // Continue without Supabase - app can still show UI
    }
    
  } catch (error, stack) {
    debugPrint('❌ Initialization error: $error\n$stack');
    // Continue anyway - app can still work with basic functionality
  }

  // Log uncaught framework errors (prevents silent white screens)
  FlutterError.onError = (FlutterErrorDetails details) {
    FlutterError.dumpErrorToConsole(details);
  };

  runApp(const SafeHerApp());
}

class SafeHerApp extends StatelessWidget {
  const SafeHerApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'SafeHer',
      theme: AppTheme.light(),
      home: const AuthWrapper(),
      debugShowCheckedModeBanner: false,
    );
  }
}

class AuthWrapper extends StatelessWidget {
  const AuthWrapper({super.key});

  @override
  Widget build(BuildContext context) {
    final session = SupabaseConfig.client.auth.currentSession;
    if (session != null) {
      return const ManageContactsScreen();
    }
    return const SafeHerWelcomeScreen();
  }
}

